const r=document.querySelectorAll(".hhh_squaresRow");r.forEach(t=>{const e=t.querySelector(".hhh_square1"),s=t.querySelector(".hhh_square3"),h=t.querySelector(".hhh_square2");e.addEventListener("mouseenter",()=>{e.classList.add("hhh_shiftRight"),h.classList.add("hhh_shiftLeft")}),e.addEventListener("mouseleave",()=>{e.classList.remove("hhh_shiftRight"),h.classList.remove("hhh_shiftLeft")}),s.addEventListener("mouseenter",()=>{s.classList.add("hhh_shiftLeft"),h.classList.add("hhh_shiftRight")}),s.addEventListener("mouseleave",()=>{s.classList.remove("hhh_shiftLeft"),h.classList.remove("hhh_shiftRight")})});
